import React, { Component, useState } from 'react';
import { Button, FormControl, Input, InputLabel } from '@material-ui/core';
export class Todo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            todo: [],
            input: '',
        }
    }
    updateIput = (event) => {
        this.setState({
            input: event.target.value,
        })
    }
    onSubmit = (event) => {
        event.preventDefault();
        this.setState({
            todo: this.state.todo.concat(this.state.input),
        })
        this.setState({
            input: '',
        })
    }
    render() {
        return (
            <>
            <form>
                <FormControl>
                    <InputLabel>Write TODO</InputLabel>
                    <Input value={this.state.input} onChange={this.updateIput} placeholder='Enter Todo' />
                </FormControl>
                <Button disabled type="submit" style={{ backgroundColor: 'blue', marginTop: '10px' }} onClick={this.onSubmit}>Submit</Button>
            </form>
                <ul>
                    {this.state.todo.map((todo) => (
                        <li>
                            {todo}
                        </li>
                    ))}
                </ul>
            </>
        )
    }
}
