    const [userLiked, setUserLiked] = useState(null);
    const [us, setUs] = useState(null);

    useEffect(() => {
        setUserLiked(props.userLiked);
    }, [])

    const handleLike = ()=>{
            if(!likeFlag && userLiked!=us) {
                setLike(like+1);
                setLikeFlag(true);
            } else {
                if(like>0 && userLiked!=us ) {
                    setLike(like-1);
                    setLikeFlag(false);
                } else if(like===0 && userLiked!=us ){
                    setLike(0);
                    setLikeFlag(false);
                }
            }
        setUs(userLiked);
        console.log(props.userLiked);
        console.log(auth.currentUser.uid);
    }

    return (
        <div>
            <button onClick={handleLike}>Like</button><p>{like}</p>
        </div>
    )
}

