import { Avatar, Button, Input } from '@material-ui/core'
import React, { useEffect, useState } from 'react'
import { db } from '../firebase'
import './Post.css'
function Posts(props) {
    const [comments, setComments] = useState(['Nixe']);
    const [comment, setComment] = useState('');
    // useEffect(() => {
    //     db.collection('posts').doc(props.postID).collection('comments').onSnapshot(snapshot=>{
    //         setComments(snapshot.docs.map(doc=>({id: doc.id, comment: doc.data()})));
    //     })
    // }, [props.postID])
    return (
        <div className='post'>
            <div className='post__header'>
                <Avatar />
                <h3 className='post__headerText'>{props.username}</h3>
            </div>
            <div className='post__caption'>{props.caption}</div>
            <img className='post__image' src={props.imageURL} alt='' />
            {
                comments.map((comment)=>{
                    // <p key={id}>{comment.comment}</p>
                    <p>{comment}</p>
                })
            }
            <Input value='' placeholder='Write Comment' onChange={(event)=>setComment(event.target.value)}/>
            <Button color='primary' variant='contained'>Post</Button>
        </div>
    )
}

export default Posts
