import { Avatar, Button, Input } from '@material-ui/core'
import React, { useEffect, useState } from 'react'
import { db } from '../firebase'
import './Post.css'
function Posts(props) {
    const [showComment, setShowComment] = useState([]);
    useEffect(() => {
        db.collection('posts').doc(props.postID).collection('comments').onSnapshot(snapshot=>{
            setComment(snapshot.docs.map(doc=>({id: doc.id, post: doc.data()})));
        })
    }, [props.postID])
    return (
        <div className='post'>
            <div className='post__header'>
                <Avatar />
                <h3 className='post__headerText'>{props.username}</h3>
            </div>
            <div className='post__caption'>{props.caption}</div>
            <img className='post__image' src={props.imageURL} alt='' />
            {
                comment.map(({id, comment})=>{
                    <p key={id}>{comment}</p>
                })
            }
            <Input value={comment} placeholder='Write Comment' onChange={(event)=>setComment(event.target.value)}/>
            <Button color='primary' variant='contained'>Post</Button>
        </div>
    )
}

export default Posts
