import { Avatar, Button, Input } from '@material-ui/core'
import React, { useEffect, useState } from 'react'
import { db } from '../firebase'
import firebase from 'firebase';
import './Post.css'
function Posts(props) {
    const [comments, setComments] = useState([]);
    const [comment, setComment] = useState('');
    useEffect(() => {
        const unsubscribe = db.collection('posts').doc(props.postID).collection('comments').orderBy('timestamp','asc').onSnapshot(snapshot=>{
            setComments(snapshot.docs.map(doc=>({id: doc.id, comment: doc.data()})));
        })
        return ()=>{
            unsubscribe();
        }
    }, [props.postID])
    const postComment = ()=>{
        db.collection('posts').doc(props.postID).collection('comments').add({
            timestamp: firebase.firestore.FieldValue.serverTimestamp(),
            comment: comment,
        })
    }
    return (
        <div className='post'>
            <div className='post__header'>
                <Avatar />
                <h3 className='post__headerText'>{props.username}</h3>
            </div>
            <div className='post__caption'>{props.caption}</div>
            <img className='post__image' src={props.imageURL} alt='' />
            {
                comments.map(({id, comment})=>{
                    return(
                        <p key={id}>{comment.comment}</p>   
                    )
                })
            }
            <Input value={comment} placeholder='Write Comment' onChange={(event)=>setComment(event.target.value)}/>
            <Button color='primary' variant='contained' onClick={postComment}>Post</Button>
        </div>
    )
}

export default Posts
