import './App.css';
import Post from './Components/Post';
import { useState, useEffect } from 'react';
import { db } from './firebase';
function App() {
  const [posts, setPosts] = useState([
    {
      username:'Ahmad',
      caption: 'Dope', 
      imageUrl:'https://www.freecodecamp.org/news/content/images/size/w2000/2020/02/Ekran-Resmi-2019-11-18-18.08.13.png'
    },
    {
      username:'Ali',
      caption:'Lit',
      imageUrl:'https://thumbs.dreamstime.com/z/panorama-beautiful-green-forest-summer-nature-scenery-yellow-wild-flowers-panorama-beautiful-green-forest-landscape-131579660.jpg'
    },
    {
      username:'Abdullah',
      caption:'On Fire',
      imageUrl:'https://thumbs.dreamstime.com/z/winter-landscape-fair-trees-under-snow-scenery-tourists-christmas-holidays-trampled-path-snowdrifts-winter-108808354.jpg'
    }
  ]);

  //UseEffect runs a piece of code depending upon a specific condition

  useEffect(()=>{
    db.collection('posts').onSnapshot(snapshot=>{
      setPosts(snapshot.docs.map(doc=>({username:doc.username, caption: doc.caption, imageUrl:doc.imageUrl})))
    })
  },[])

  return (
    <div className='app'>
      <div className='app__header'>
        <img className='app__headerImage' src='https://assets.website-files.com/5c75b94c8dd1ae50d3b9294b/5d48831280adb734a5db5620_hukglfkfklk%3B-p-500.png' alt=''/>
      </div>
      {
        posts.map(post=>{
          return(
          <Post username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
          )
        })
      }
    {/* Header */}
    {/* posts */}
    {/* posts */}

    </div>
  );
}
export default App;
