import React, { useState } from 'react'
import { Button } from '@material-ui/core';
import { db, storage } from '../firebase'
import firebase from 'firebase';
function ImageUpload(props) {
    const [image, setImage] = useState(null);
    const [caption, setCaption] = useState('');
    const [progress, setProgress] = useState(0);
    const [URL, setURL] = useState(null);                                                                                                                                           
    const handleChange = (event)=>{
        if(event.target.files[0]){
            setImage(event.target.files[0])
            console.log(event.target.files[0]);
        }
    }

    const handleUpload = ()=>{
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        uploadTask.on(
            'state_changed',
            (snapshot)=>{
                const progress = Math.round((snapshot.bytesTransferred / snapshot.totalBytes)*100);
                setProgress(progress);
            },

        (error)=>{
            console.log(error.message);
            alert(error.message);
        },
        ()=>{
            storage.ref('images').child(image.name).getDownloadURL().then(url=>{
                db.collection('posts').add({
                    timestamp: firebase.firestor.fieldValue.serverTimestamp(),
                    caption: caption,
                    username: props.username,
                    imageUrl: url,
                })
            })
        })
    }

    // function handleUpload(e) {
    //     e.preventDefault();
    //     const uploadTask = storage.ref(`/images/${image.name}`).put(image);
    //     uploadTask.on("state_changed", console.log, console.error, () => {
    //       storage
    //         .ref("images")
    //         .child(image.name)
    //         .getDownloadURL()
    //         .then((url) => {
    //           setImage(null);
    //           setURL(url);
    //         });
    //     });
    //   }

    return (
        <div>
            <h3>Image Uploader</h3>
            <input type='text' value={caption} placeholder='Write Caption Here' onChange={(event)=>setCaption(event.target.value)}/>
            <input type='file' onChange={handleChange}/>
            <Button color='primary' variant='outlined' onClick={handleUpload}>Upload</Button>
        </div>
    )
}

export default ImageUpload
