import React, { forwardRef } from 'react'
import { Card, CardContent, Typography } from '@material-ui/core';
import './message.css'
const Message = forwardRef(({ message, username, user }, ref) => {
    const isUser = username === message.username;
    const getClass = ()=>{
        if(user) {
            return 'message__user'
        } else {
            return 'message'
        }
    }
