import './App.css';
import { useState, useEffect } from 'react';
import { Button, Input, InputLabel } from '@material-ui/core';
import { FormControl } from '@material-ui/core';
import Message from './Components/Message';
function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([{username:'', text:''}]);
  const [username, setUsername] = useState('');
  useEffect(() => {
    setUsername(prompt('Please Enter Your Name.'));
  }, [])

  const handleChange = (event) => {
    const inp = event.target.value;
    setInput(inp);
  }
  const sendMessage = (event) => {
    event.preventDefault();
    setMessages([...messages, username=username]);
    setInput('');
  }

  return (
    <div className="App">
      <h1>Hello World</h1>
      <h2>Welcome {username}</h2>
      <form>
        <FormControl>
          <InputLabel>Type Message</InputLabel>
          <Input value={input} onChange={handleChange} />
        </FormControl>
        <Button disabled={!input} type='submit' onClick={sendMessage} color='primary' variant='contained'>Send</Button>
      </form>
      {
        messages.map(message => (<Message username={message.username} text={message.text} />))
      }
    </div>
  );
}

export default App;
