import './App.css';
import { useState, useEffect } from 'react';
import { Button, Input, InputLabel } from '@material-ui/core';
import { FormControl } from '@material-ui/core';
import Message from './Components/Message';
import db  from './firebase';
import firebase from 'firebase'
import FlipMove from 'react-flip-move';
import SendIcon from '@material-ui/icons/Send';
function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([{username:'', message:''}]);
  const [username, setUsername] = useState('');

  useEffect(() => {
    setUsername(prompt('Please Enter Your Name.'));
  }, [])

  useEffect(() => {
    db.collection('messages').orderBy('timestamp','desc').onSnapshot(snapshot=>{
      setMessages(snapshot.docs.map(doc=>({id: doc.id , message: doc.data()})))
    })
  },[])

  const handleChange = (event) => {
    const inp = event.target.value;
    setInput(inp);
  }
  const sendMessage = (event) => {
    event.preventDefault();
    // To Add messages Locally
    // setMessages([...messages, {username:username, text:input}]);

    //To add messages from Database
    db.collection('messages').add({
      message:input,
      username:username,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
    })
    setInput('');
  }

  return (
    <div className="App">
      <div className='app__header'>
        <img src='https://seeklogo.com/images/F/facebook-messenger-logo-36376366E2-seeklogo.com.png?w=100&h=100'/>
        <h1>Hello World</h1>
        <h2>Welcome {username}</h2>
      </div>
      <form className='app__form'>
        <FormControl className='app__formControl'>
          <InputLabel className='app__inputLabel'>Type Message</InputLabel>
          <Input value={input} onChange={handleChange} />
          <IconButton >

          </IconButton>
        <Button className='app__button' disabled={!input} type='submit' onClick={sendMessage} color='primary' variant='contained'>Send</Button>
        </FormControl>
      </form>
      <FlipMove>
      {
        messages.map(({id, message}) => (<Message key={id} username={username} message={message} />))
      }
      </FlipMove>
    </div>
  );
}

export default App;
