import React, { Component } from 'react';
export class MyForm extends Component {
    state = {
        name: '',
        text: '',
        checkbox: 'false',
        opt: '',
    };
    handleChange = (event, fieldName) => {
        console.log(fieldName);
        this.setState({ [fieldName]: event.target.value })
    };
    handleCheck = (event) => {
        this.setState({ checkbox: event.target.checked })
    }
    handleOption = (event) => {
        console.log(this.state.opt);
        this.setState({ opt: event.target.value })
    }
    render() {
        return (
            <>
                <input value={this.state.name} onChange={(event) => { this.handleChange(event, 'name') }} />
                <textarea value={this.state.text} onChange={(event) => { this.handleChange(event, 'text') }} />
                <input type="checkbox" checked={this.state.checkbox} onChange={this.handleCheck} />
                <select value={this.state.opt} onChange={(event)=>{this.handleChange(event,'opt')}}>
                    <option>Ahmad</option>
                    <option>Ali</option>
                    <option>Abdullah</option>
                </select>
            </>
        )
    }
}
