export class Counter extends Component {
    constructor(props) {
        super(props);
        this.state = {
            names: [
                "Ahmad Butt",
                "Ali Butt",
                "Abdullah Butt",
                "Akram Butt",
            ], index: props.initIndex,
        }
    }
    nextName = () => {
        if (this.state.index >= 0 && this.state.index < 3) {
            this.setState({
                index: this.state.index + 1,
            })
        }
        else {
            this.setState({
                index: 0,
            })
        }
    }
    render() {
        return (
            <div>
                <h1>{this.state.names[this.state.index]}</h1>
                <button onClick={this.nextName}>Next Name</button>
            </div>
        )
    }
}
