import React, { useEffect, useState } from 'react'
import { useStateValue } from '../StateProvider';
function SubTotal() {
    const [{ basket, totalPrice }] = useStateValue();
    return (
        <div>
            <p>Subtotal ({`${basket?.length}`} items) <strong>{totalPrice}</strong></p>
        </div>
    )
}

export default SubTotal
