export const initialState = {
    basket: [],
};

function reducer(state, action) {
    console.log(basket);
    switch(action.type) {
        case 'ADD_TO_BASKET':{
            //Logic
            return { state };
            break;
        }
        case 'REMOVE_FROM_BASKET':
            //Logic
            return { state }
            break;
        default:
            return state;
    }
}
export default reducer;
