import React, { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState(['Take dogs for a walk','Take the rubbish out','Turn off Refrigerator']);
  const [input, setInput] = useState('')
  console.log(input);
  const addTodo = (event)=>{
    event.preventDefault();
    console.log('Hey Ahmad!!!');
    setTodos([...todos,input]); //spread function which appends todo state
    setInput(''); //Clear input after hitting submit
  }
  return (
    <div className="App">
      <h1>Hello World</h1>
      <form>
        <input value={input} onChange={event => setInput(event.target.value)}/>
        <button variant="contained" color="primary" onClick={addTodo}>Add TODO</button>
      </form>
      <ul>
        {todos.map(todo=>(
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
//Important things are state and props
// JSX,we can write dynamic javascript aswell
//state => short term memory when we refresh the state is cleared
